SELECT uuid, account, amount, currency, receiver1, receiver2, description, firstname, lastname, status, transfer_date as transferDate, valuta_date as valutaDate, order_id as orderId, client_id as clientId, invoice_id as invoiceId, amount_diff as amountDiff, ebay_name as ebayName FROM bank_statement WHERE transfer_date >= :from AND transfer_date <= :until AND status LIKE :status AND order_id LIKE :orderId AND firstname LIKE :firstname AND lastname LIKE :lastname